import "./styles.css";
import { Toast } from "primereact/toast";
import { useRef } from "react";
import { ErrorShow } from "../errors/errorsServer";
interface ErrorsMessages {
  messages?: string;
}
export const ErrorsInputs = ({ messages }: ErrorsMessages) => (
  <div className="space-items error-inputs">{messages}</div>
);

export const ToastErrors = ({ errorsInputs, errorsServer }: ErrorShow) => {
  const toast = useRef<Toast>(null);
  if (errorsInputs?.data.errors) {
    errorsInputs?.data.errors.map((data: any) => {
      toast?.current?.show({
        severity: "error",
        summary: "Error Message",
        detail: `${data.msg}`,
        life: 3000,
      });
    });
  }
  if (errorsServer) {
    toast?.current?.show({
      severity: "error",
      summary: "Error Message",
      detail: `${errorsServer.data.errors}`,
      life: 3000,
    });
  }
  return <Toast ref={toast} position="bottom-left" />;
};
