import "./styles.css";
import { Toast } from "primereact/toast";
import { useRef } from "react";
interface ErrorsMessages {
  messages?: string;
}
export const ErrorsInputs = ({ messages }: ErrorsMessages) => (
  <div className="space-items error-inputs">{messages}</div>
);

export const ToastMessage = (status: number, message: string) => {
  const toast = useRef(null);
  if (status >= 400) {
    showError(toast, message);
  } else {
    showSuccess(toast, message);
  }
};

const showSuccess = (toast: any, message: string) => {
  toast.current.show({
    severity: "success",
    summary: "Success Message",
    detail: message,
    life: 3000,
  });
  return <Toast ref={toast} />;
};

const showError = (toast: any, message: string) => {
  toast.current.show({
    severity: "error",
    summary: "Error",
    detail: message,
    life: 3000,
  });
  return <Toast ref={toast} />;
};
