import React, { useState } from "react";
import { ILogin } from "../../models/Ilogin.interface";

export const loginFormHooks = () => {
  const [loginInput, setLoginInput] = useState<ILogin>({
    username: "",
    password: "",
  });
  /* CHANGE */
  const useChangeInput = (e: React.ChangeEvent<HTMLInputElement>) => {
    setLoginInput({
      ...loginInput,
      [e.target.name]: e.target.value,
    });
  };
  /* HANDLESUBMIT */
  const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    console.log(loginInput);
  };
  return { useChangeInput, handleSubmit };
};
