import { useState } from "react";

export const usePermissions = () => {
  const [users, setUsers] = useState<boolean>(false);
  const tokenUsers = localStorage.getItem("tokenUser");
  if (tokenUsers) {
    setUsers(true);
  } else {
    setUsers(false);
  }
  return { users };
};
