import { Toast } from "primereact";
import { useEffect, useRef } from "react";
import { IErrorsServer } from "./errors.models";

export const ToastErrors = ({ errors }: IErrorsServer) => {
  const toast = useRef<Toast>(null);
  const succes = () => {
    if (typeof errors === "string" || errors instanceof String) {
      toast?.current?.show({
        severity: "error",
        summary: "Error",
        detail: errors,
        life: 3000,
      });
    } else {
      errors.map((value: any) => {
        toast?.current?.show({
          severity: "error",
          summary: "Error",
          detail: value.msg,
          life: 3000,
        });
      });
    }
  };
  useEffect(() => {
    succes();
  }, [toast]);
  return <Toast ref={toast} />;
};
