import { SubmitHandler, useForm } from "react-hook-form";
import { useNavigate } from "react-router-dom";
import { ILogin } from "../../models/Ilogin.interface";
import { postLogin } from "../../services/login.service";
import urlFront from "../../../shared/routes/routesFrontend.routes";
import { usePermissions } from "../../../shared/hooks/usePermision";

export const LoginFormHooks = () => {
  const {
    register,
    handleSubmit,
    control,
    formState: { errors },
  } = useForm<ILogin>();
  const navigate = useNavigate();
  /* HANDLESUBMIT */
  const onSubmit: SubmitHandler<ILogin> = async (data: ILogin) => {
    const resp = await postLogin(data);
    if (resp?.status === 200) {
      usePermissions();
      navigate(`${urlFront.module}${urlFront.main}`);
    }
  };

  return { register, handleSubmit, onSubmit, errors, control };
};
