import { Toast } from "primereact";
import { useEffect, useRef } from "react";
import "./styles.css";

interface ErrorsMessages {
  messages?: string;
}
export const ErrorsInputs = ({ messages }: ErrorsMessages) => (
  <div className="space-items error-inputs">{messages}</div>
);

export const ToastErrors = () => {
  const toast = useRef<Toast>(null);
  const succes = () => {
    console.log("success");
    toast?.current?.show({
      severity: "success",
      summary: "Success Message",
      detail: "Message Content",
      life: 3000,
    });
  };
  useEffect(() => {
    succes();
  }, [toast]);
  return <Toast ref={toast} />;
};
