import { Toast } from "primereact";
import { useRef } from "react";
import { SubmitHandler, useForm } from "react-hook-form";
import { ILogin } from "../../models/Ilogin.interface";
import { postLogin } from "../../services/login.service";

export const LoginFormHooks = () => {
  const {
    register,
    handleSubmit,
    control,
    formState: { errors },
  } = useForm<ILogin>();
  /* HANDLESUBMIT */
  const toast = useRef<Toast>(null);
  const onSubmit: SubmitHandler<ILogin> = async (data: ILogin) => {
    const resp = await postLogin(data);
    toast?.current?.show({
      severity: "success",
      summary: "Success Message",
      detail: "Message Content",
      life: 3000,
    });
  };

  return { register, handleSubmit, onSubmit, errors, control, toast };
};
