import { option } from "../../shared/config/optionPeition";
import urlApi from "../../shared/routes/routesAPi.routes";
import { ILogin } from "../models/Ilogin.interface";

export const getAllUsers = async (): Promise<Array<ILogin>> => {
  /* urlApi.api + urlApi.login */
  const data = await fetch(
    "https://jsonplaceholder.typicode.com/users",
    option("GET")
  );
  const resp = await data.json();
  return resp;
};

export const postAllUsers = async (form: ILogin, token: string) => {
  const data = await fetch(
    urlApi.api + urlApi.login,
    option("POST", form, token)
  );
  const resp = await data.json();
  return resp;
};

export const postLogin = async (form: ILogin) => {
  const data = await fetch(urlApi.api + urlApi.login, option("POST", form));
  const resp = await data.json();
  return resp;
};
