import React, { useState } from "react";
import { ILogin } from "../../models/Ilogin.interface";
import { postLogin } from "../../services/login.service";

export const LoginFormHooks = () => {
  const [loginInput, setLoginInput] = useState<ILogin>({
    username: "",
    password: "",
  });
  const validateInputs = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.value.length >= 5 && e.target.value.length <= 20) {
      console.log("error falta caracteres");
    }
    return;
  };
  /* CHANGE */
  const useChangeInput = (e: React.ChangeEvent<HTMLInputElement>) => {
    validateInputs(e);
    setLoginInput({
      ...loginInput,
      [e.target.name]: e.target.value,
    });
  };
  /* HANDLESUBMIT */
  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const resp = await postLogin(loginInput);
    console.log(resp);
  };
  return { useChangeInput, handleSubmit };
};
