export const option = (method: string, data?: any, token?: string): {} => {
  if (token) {
    return {
      method: method,
      headers: {
        "Content-Type": "application/json;charset=utf-8",
        "x-access-token": token,
      },
      body: data ? JSON.stringify(data) : "",
    };
  } else {
    return {
      method: method,
      headers: {
        "Content-Type": "application/json;charset=utf-8",
      },
      body: data ? JSON.stringify(data) : "",
    };
  }
};
