import { ILogin } from "../models/Ilogin.interface";

interface ErrorsInputs {
  type?: string;
  msg?: string;
}

export const validateLoginInput = (form: ILogin) => {
  let errors: ErrorsInputs = {};
  if (form.username.trim()) {
    errors = {
      type: "required",
      msg: "El campo username es requerido",
    };
    return errors;
  }
  if (form.username.length > 5 && form.username.length < 10) {
    errors = {
      type: "longitud",
      msg: "El campo username debe tener una longitud entre 5 a 10 caracteres",
    };
    return errors;
  }
  if (form.username.match(/^[^$&{}|<>]*$/)) {
    errors = {
      type: "longitud",
      msg: "El campo username no debe tener caracteres especiales $&{}|<>",
    };
    return errors;
  }
};
