import React, { useState } from "react";
import { ILogin } from "../models/Ilogin.interface";

export const loginFormHooks = () => {
  const [loginInput, setLoginInput] = useState<ILogin>({
    username: "",
    password: "",
  });
  const useStateInputLogin = (e: React.ChangeEvent<HTMLInputElement>) => {
    setLoginInput({
      ...loginInput,
      [e.target.name]: e.target.value,
    });
  };
  const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    console.log(loginInput);
  };
};
