import { InputText, Password } from "primereact";
import { Controller } from "react-hook-form";
import { ButtonPrimary } from "../../../shared/components/buttons/ButtonPrimary";
import { ErrorsInputs } from "../../../shared/messages/errorsMessages";
import {
  passwordValidate,
  usernameValidate,
} from "../../validations/validateInputs";
import { LoginFormHooks } from "./Form.component";
import { Toast } from "primereact/toast";
import "./styles.css";
import { useRef } from "react";

export const FormHtml = () => {
  const { handleSubmit, onSubmit, register, errors, control } =
    LoginFormHooks();
  const toast = useRef<Toast>(null);
  const showSuccess = () => {
    toast?.current?.show({
      severity: "success",
      summary: "Success Message",
      detail: "Message Content",
      life: 3000,
    });
  };
  return (
    <div className="container">
      <div className="items">
        <h1>Login</h1>
      </div>
      <div className="items">
        <form action="" onSubmit={handleSubmit(onSubmit)}>
          <h6>Usuario</h6>
          <span className="p-input-icon-left">
            <i className="pi pi-user" />
            <InputText
              {...register("username", usernameValidate)}
              placeholder="Username"
              autoFocus
            />
          </span>
          <ErrorsInputs key={"username"} messages={errors.username?.message} />
          <h6>Contrase√±a</h6>
          <Controller
            name="password"
            control={control}
            rules={passwordValidate}
            render={({ field, field: { ref } }) => (
              <Password
                {...field}
                placeholder="Password"
                value={field.value || ""}
                inputRef={ref}
                toggleMask
              />
            )}
          />
          <ErrorsInputs key={"password"} messages={errors.password?.message} />
          <div className="space-items">
            <ButtonPrimary
              type="submit"
              icon="pi pi-check"
              iconsPos="left"
              label="Login"
              styles={{ width: "100%" }}
            />
          </div>
        </form>

        <button
          value="Success"
          className="p-button-success"
          onClick={showSuccess}
        />
      </div>
    </div>
  );
};
