import "./styles.css";
import { Toast } from "primereact/toast";
import { useRef } from "react";
import { ErrorsInputsServer, ErrorsServer } from "../errors/errorsServer";
interface ErrorsMessages {
  messages?: string;
}
export const ErrorsInputs = ({ messages }: ErrorsMessages) => (
  <div className="space-items error-inputs">{messages}</div>
);

export const ToastShow = () => {
  const toast = useRef<Toast>(null);

  const errorsMessage = (
    errosCampus: ErrorsInputsServer,
    errorsServers: ErrorsServer
  ) => {
    if (errosCampus.data.errors) {
      errosCampus.data.errors.map((data: any) => {
        toast?.current?.show({
          severity: "error",
          summary: "Error Message",
          detail: `${data.msg}`,
          life: 3000,
        });
      });
    }
    if (errorsServers) {
      toast?.current?.show({
        severity: "error",
        summary: "Error Message",
        detail: `${errorsServers.data.errors}`,
        life: 3000,
      });
    }
  };
  return <Toast />;
};
