import "./styles.css";
import { Toast } from "primereact/toast";
import { useRef } from "react";
interface ErrorsMessages {
  messages?: string;
}
export const ErrorsInputs = ({ messages }: ErrorsMessages) => (
  <div className="space-items error-inputs">{messages}</div>
);

export const ToastMessage = (message: string) => {
  const toast = useRef<Toast>(null);

  const showSuccess = () => {
    toast?.current?.show({
      severity: "success",
      summary: "Success Message",
      detail: message,
      life: 3000,
    });
  };

  const showError = (toast: any, message: string) => {
    toast.current.show({
      severity: "error",
      summary: "Error",
      detail: message,
      life: 3000,
    });

    return <Toast ref={toast} />;
  };
};

