import React, { useContext } from "react";
import { EmployeContext } from "../../context/employe.context";
import { Column, DataTable, DataTableSelectionChangeParams } from "primereact";
import "./styles.css";
export const ListEmployee = () => {
  const { employe } = useContext(EmployeContext);

  const setSelectedProducts2 = (e: DataTableSelectionChangeParams) => {
    console.log(e);
  };
  /* TRANSFORM DINERS */
  const trasnformDinner = (rowData: any) => {
    return `${formatCurrency(rowData.lastYearProfit)}`;
  };
  /* FORMAT DINNERS */
  const formatCurrency = (value: any) => {
    return value.toLocaleString("en-US", {
      style: "currency",
      currency: "USD",
    });
  };
  return (
    <React.Fragment>
      <h3>List Employee</h3>
      <div className="card">
        <DataTable
          value={employe}
          stripedRows
          selectionMode="single"
          dataKey="id"
          onSelectionChange={(e) => setSelectedProducts2(e.value)}
          responsiveLayout="scroll"
        >
          <Column field="id_employee" header="Identificacion"></Column>
          <Column field="id_person" header="Nombre persona"></Column>
          <Column field="deparment" header="Departamento"></Column>
          <Column
            field="salary"
            header="Salario"
            body={trasnformDinner}
          ></Column>
          <Column field="hours_extra" header="Horas Extras"></Column>
        </DataTable>
      </div>
    </React.Fragment>
  );
};
