import React, { useEffect, useState } from "react";
import { ILogin } from "../models/Ilogin.interface";
import { getAllUsers } from "../services/login.service";

export const useEffectLogin = () => {
  /* METHOD PETITION FETCH DATA */
  const [user, setUser] = useState<Array<ILogin>>([]);
  const fetchData = async () => {
    const data = await getAllUsers();
    setUser(data);
  };
  useEffect(() => {
    fetchData();
  }, []);
  return user;
};

export const useStateInputLogin = (
  evt: React.ChangeEvent<HTMLInputElement>
) => {
  const [loginInput, setLoginInput] = useState<ILogin>({
    username: "",
    password: "",
  });
  setLoginInput({
    ...loginInput,
    [evt.target.name]: evt.target.value,
  });
  return loginInput;
};
